mapper.select:
select count(id)
from user
where id = #{id}

select id, name, email, phone
from user
where id = #{userid} and pass = #{userpwd}

select name, id, email, phone, pass
from user
where id = #{userid}

mapper.update:
update user
set pass = #{pass},
	name = #{name},
	email = #{email},
	phone = #{phone}
where id = #{id}
